from typing import List, Tuple
import random

# Global override: all training data weights are 1x
FORCE_UNIFORM_TRAINING_WEIGHTS = True


def _dedupe_pairs(pairs: List[Tuple[str, str]]) -> List[Tuple[str, str]]:
    """Deduplicate training pairs while preserving order."""
    if not FORCE_UNIFORM_TRAINING_WEIGHTS:
        return pairs
    seen = set()
    unique: List[Tuple[str, str]] = []
    for prompt, response in pairs:
        key = (prompt, response)
        if key in seen:
            continue
        seen.add(key)
        unique.append((prompt, response))
    return unique

