def create_training_corpus(num_examples: int = 10000, focused: bool = False, balanced: bool = False) -> str:
    """Create training corpus by repeating and shuffling examples.

    Args:
        num_examples: Number of examples to include
        focused: If True, use minimal focused training data
        balanced: If True, use balanced general-purpose data (less security)
    """
    all_data = get_all_training_data(focused=focused, balanced=balanced)

    # Create formatted examples with special tokens for proper turn boundaries
    examples = []
    for prompt, response in all_data:
        # Format with special tokens for user/assistant turns
        formatted = f" <|user|> \n{prompt}\n <|end_turn|> \n <|assistant|> \n{response}\n <|end_turn|> "
        examples.append(formatted)

    if FORCE_UNIFORM_TRAINING_WEIGHTS:
        random.shuffle(examples)
        limit = min(num_examples, len(examples))
        return "\n\n\n".join(examples[:limit])

    # Repeat and shuffle
    corpus = []
    for _ in range(num_examples // len(examples) + 1):
        shuffled = examples.copy()
        random.shuffle(shuffled)
        corpus.extend(shuffled)

    # Join with clear separator to prevent response bleeding between examples
    # Using triple newlines and a visual break helps model learn boundaries
    return "\n\n\n".join(corpus[:num_examples])
