# =====================================================================================================================


def gtlt(a, b, c):
    "Same as a < b < c."
    return a < b < c


def gtle(a, b, c):
    "Same as a < b <= c."
    return a < b <= c


def gelt(a, b, c):
    "Same as a <= b < c."
    return a <= b < c


def gele(a, b, c):
    "Same as a <= b <= c."
    return a <= b <= c


