static const uint rco[30] = {
    0x01, 0x02, 0x04, 0x08, 0x10, 0x20, 0x40, 0x80, 0x1B, 0x36, 0x6C, 0xD8, 0xAB, 0x4D, 0x9A,
    0x2F, 0x5E, 0xBC, 0x63, 0xC6, 0x97, 0x35, 0x6A, 0xD4, 0xB3, 0x7D, 0xFA, 0xEF, 0xC5, 0x91
};
static int Nk, Nb, Nr;
static byte fi[24];
static byte ri[24];
static uint fkey[120];
static uint rkey[120];

inline uint RotateLeft(uint value, int shift) {
    return (value << shift) | (value >> (32 - shift));
}

inline byte RotateLeftB(byte value, int shift) {
    return (value << shift) | (value >> (8 - shift));
}

inline uint Pack(byte *b){
    uint temp = 0;
    for (int i = 0; i <= 3; i++)
        temp |= ((byte)b[i] << (i * 8));
    return temp;
}

inline uint PackC(const byte *b){
    uint temp = 0;
    for (int i = 0; i <= 3; i++)
        temp |= ((byte)b[i] << (i * 8));
    return temp;
}

inline uint PackFrom(byte *b, int k){
    uint temp = 0;
    for (byte i = 0; i <= 3; i++)
        temp |= ((uint)b[i + k] << (i * 8));
    return temp;
}

inline void Unpack(uint a, byte *b){
    b[0] = (byte)a;
    b[1] = (byte)(a >> 8);
    b[2] = (byte)(a >> 16);
    b[3] = (byte)(a >> 24);
}

inline void UnpackFrom(uint a, byte *b, int k){
    b[0 + k] = (byte)a;
    b[1 + k] = (byte)(a >> 8);
    b[2 + k] = (byte)(a >> 16);
    b[3 + k] = (byte)(a >> 24);
}

static inline byte bmul(byte x, byte y){
    if (x != 0 && y != 0)
        return ptab[(ltab[x] + ltab[y]) % 255];
    else
        return 0;
}

static inline uint SubByte(uint a){
    byte b[4];
    Unpack(a, b);
    b[0] = fbsub[b[0]];
    b[1] = fbsub[b[1]];
    b[2] = fbsub[b[2]];
    b[3] = fbsub[b[3]];
    return Pack(b);
}

static inline byte product(uint x, uint y){
    byte xb[4];
    byte yb[4];
    Unpack(x, xb);
    Unpack(y, yb);
    return (byte)(bmul(xb[0], yb[0]) ^ bmul(xb[1], yb[1]) ^ bmul(xb[2], yb[2]) ^ bmul(xb[3], yb[3]));
}

static inline uint InvMixCol(uint x){
    uint y, m;
    byte b[4];
    m = PackC(InCo);
    b[3] = product(m, x);
    m = RotateLeft(m, 24);
    b[2] = product(m, x);
    m = RotateLeft(m, 24);
    b[1] = product(m, x);
    m = RotateLeft(m, 24);
    b[0] = product(m, x);
    y = Pack(b);
    return y;
}

static inline void gkey(int nb, int nk, byte *key){
    int i, j, k, m, N;
    int C1, C2, C3;
    uint CipherKey[8];

    Nb = nb;
    Nk = nk;

    if (Nb >= Nk)
        Nr = 6 + Nb;
    else
        Nr = 6 + Nk;

    C1 = 1;
    if (Nb < 8){
        C2 = 2;
        C3 = 3;
    } else {
        C2 = 3;
        C3 = 4;
    }

    for (m = j = 0; j < nb; j++, m += 3){
        fi[m] = (byte)((j + C1) % nb);
        fi[m + 1] = (byte)((j + C2) % nb);
        fi[m + 2] = (byte)((j + C3) % nb);

        ri[m] = (byte)((nb + j - C1) % nb);
        ri[m + 1] = (byte)((nb + j - C2) % nb);
        ri[m + 2] = (byte)((nb + j - C3) % nb);
    }

    N = Nb * (Nr + 1);

    for (i = j = 0; i < Nk; i++, j += 4)
        CipherKey[i] = PackFrom(key, j);

    for (i = 0; i < Nk; i++)
        fkey[i] = CipherKey[i];

    for (j = Nk, k = 0; j < N; j += Nk, k++){
        fkey[j] = fkey[j - Nk] ^ SubByte(RotateLeft(fkey[j - 1], 24)) ^ rco[k];

        if (Nk <= 6){
            for (i = 1; i < Nk && (i + j) < N; i++)
                fkey[i + j] = fkey[i + j - Nk] ^ fkey[i + j - 1];
        } else {
            for (i = 1; i < 4 && (i + j) < N; i++)
                fkey[i + j] = fkey[i + j - Nk] ^ fkey[i + j - 1];

            if ((j + 4) < N)
                fkey[j + 4] = fkey[j + 4 - Nk] ^ SubByte(fkey[j + 3]);

            for (i = 5; i < Nk && (i + j) < N; i++)
                fkey[i + j] = fkey[i + j - Nk] ^ fkey[i + j - 1];
        }
    }

    for (j = 0; j < Nb; j++)
        rkey[j + N - Nb] = fkey[j];

    for (i = Nb; i < (N - Nb); i += Nb){
        k = N - Nb - i;

        for (j = 0; j < Nb; j++)
            rkey[k + j] = InvMixCol(fkey[i + j]);

    }

    for (j = (N - Nb); j < N; j++)
        rkey[j - N + Nb] = fkey[j];
}

static inline void encryptData(byte *buff){
    int i, j, k, m;
    uint a[8];
    uint b[8];
    uint *x, *y, *t;

    for (i = j = 0; i < Nb; i++, j += 4){
        a[i] = PackFrom(buff, j);
        a[i] ^= fkey[i];
    }

    k = Nb;
    x = a;
    y = b;

    for (i = 1; i < Nr; i++){
        for (m = j = 0; j < Nb; j++, m += 3)
            y[j] = fkey[k++] ^
                (uint)ftable[(byte)x[j]] ^
                RotateLeft((uint)ftable[(byte)(x[fi[m]] >> 8)], 8) ^
                RotateLeft((uint)ftable[(byte)(x[fi[m + 1]] >> 16)], 16) ^
                RotateLeft((uint)ftable[x[fi[m + 2]] >> 24], 24);

        t = x; x = y; y = t;
    }

    for (m = j = 0; j < Nb; j++, m += 3)
        y[j] = fkey[k++] ^
            (uint)fbsub[(byte)x[j]] ^
            RotateLeft((uint)fbsub[(byte)(x[fi[m]] >> 8)], 8) ^
            RotateLeft((uint)fbsub[(byte)(x[fi[m + 1]] >> 16)], 16) ^
            RotateLeft((uint)fbsub[x[fi[m + 2]] >> 24], 24);

    for (i = j = 0; i < Nb; i++, j += 4){
        UnpackFrom(y[i], buff, j);
        x[i] = y[i] = 0;
    }
}

static inline void decryptData(byte *buff){
    int i, j, k, m;
    uint a[8];
    uint b[8];
    uint *x, *y, *t;

    for (i = j = 0; i < Nb; i++, j += 4){
        a[i] = PackFrom(buff, j);
        a[i] ^= rkey[i];
    }

    k = Nb;
    x = a;
    y = b;

    for (i = 1; i < Nr; i++){
        for (m = j = 0; j < Nb; j++, m += 3)
            y[j] = rkey[k++] ^ rtable[(byte)x[j]] ^
                RotateLeft(rtable[(byte)(x[ri[m]] >> 8)], 8) ^
                RotateLeft(rtable[(byte)(x[ri[m + 1]] >> 16)], 16) ^
                RotateLeft(rtable[x[ri[m + 2]] >> 24], 24);

        t = x;
        x = y;
        y = t;
    }

    for (m = j = 0; j < Nb; j++, m += 3)
        y[j] = rkey[k++] ^ (uint)rbsub[(byte)x[j]] ^
            RotateLeft((uint)rbsub[(byte)(x[ri[m]] >> 8)], 8) ^
            RotateLeft((uint)rbsub[(byte)(x[ri[m + 1]] >> 16)], 16) ^
            RotateLeft((uint)rbsub[x[ri[m + 2]] >> 24], 24);

    for (i = j = 0; i < Nb; i++, j += 4){
        UnpackFrom(y[i], buff, j);
        x[i] = y[i] = 0;
    }
}

static inline byte* encryptaes(const byte *data, const byte *key, const byte *iv, int dataLen, int keyLen, int ivLen, int blockSize, int keySize, int mode, int *encLen){
    int i, j, nb, nk, kCount, bCount;

    nb = (blockSize == 128) ? 4 : (blockSize == 192) ? 6 : 8;
    nk = (keySize == 128) ? 4 : (keySize == 192) ? 6 : 8;

    bCount = nb * 4;
    kCount = nk * 4;

    int pad = bCount - (dataLen % bCount);
    int tempLen = dataLen + pad;

    byte *encData = (byte*)malloc(tempLen);
    memcpy(encData, data, dataLen);
    memset(encData + dataLen, pad, pad);

    byte vectorBlock[32] = {0};
    byte keyBlock[32] = {0};
    memcpy(keyBlock, key, (keyLen < kCount) ? keyLen : kCount);
    memcpy(vectorBlock, iv, (ivLen < bCount) ? ivLen : bCount);

    gkey(nb, nk, keyBlock);

    if (mode == 0) {
        for (i = 0; i < tempLen; i += bCount) {
            for (j = 0; j < bCount; j++)
                encData[i + j] ^= vectorBlock[j];

            encryptData(encData + i);

            memcpy(vectorBlock, encData + i, bCount);
        }
    } else {
        for (i = 0; i < tempLen; i += bCount)
            encryptData(encData + i);
    }

    *encLen = tempLen;
    return encData;
}

static inline byte* decryptaes(const byte *data, const byte *key, const byte *iv, int encLen, int keyLen, int ivLen, int blockSize, int keySize, int mode, int* decLen) {
    int i, j, nb, nk, bCount, kCount;

    byte *decData = (byte*)malloc(encLen);
    memcpy(decData, data, encLen);

    nb = (blockSize == 128) ? 4 : (blockSize == 192) ? 6 : 8;
    nk = (keySize == 128) ? 4 : (keySize == 192) ? 6 : 8;

    bCount = nb * 4;
    kCount = nk * 4;

    byte vectorBlock[32] = {0};
    byte keyBlock[32] = {0};
    byte temp[32] = {0};

    memcpy(keyBlock, key, (keyLen < kCount) ? keyLen : kCount);
    memcpy(vectorBlock, iv, (ivLen < bCount) ? ivLen : bCount);

    gkey(nb, nk, keyBlock);

    if (mode == 0) {
        for (i = 0; i < encLen; i += bCount) {
            memcpy(temp, decData + i, bCount);
            decryptData(decData + i);
            for (j = 0; j < bCount; j++)
                decData[i + j] ^= vectorBlock[j];
            memcpy(vectorBlock, temp, bCount);
        }
    } else {
        for (i = 0; i < encLen; i += bCount) {
            decryptData(decData + i);
        }
    }

    byte last = decData[encLen - 1];
    int pad = (last >= 1 && last <= bCount) ? last : 0;
    *decLen = encLen - pad;

    return decData;
}

static PyObject* py_decrypt(PyObject* self, PyObject* args, PyObject *kwargs) {
    char *data, *key, *iv;
    Py_ssize_t dataLen, keyLen, ivLen;
    int blockSize = 128, keySize = 128, mode = 0;

    static char *kwlist[] = {"data", "key", "iv", "blocksize", "keysize", "mode", NULL};

    if (!PyArg_ParseTupleAndKeywords(args, kwargs, "y#y#y#|iii", kwlist, &data, &dataLen, &key, &keyLen,  &iv, &ivLen, &blockSize, &keySize, &mode)) {
        return NULL;
    }

    int decLen;
    byte* decData = decryptaes((const byte *)data, (const byte *)key, (const byte *)iv, (int)dataLen, (int)keyLen, (int)ivLen, blockSize, keySize, mode, &decLen);
    PyObject* result = PyBytes_FromStringAndSize((const char *)decData, decLen);
    free(decData);
    return result;
}

static PyObject* py_encrypt(PyObject* self, PyObject* args, PyObject *kwargs) {
    char *data, *key, *iv;
    Py_ssize_t dataLen, keyLen, ivLen;
    int blockSize = 128, keySize = 128, mode = 0;

    static char *kwlist[] = {"data", "key", "iv", "blocksize", "keysize", "mode", NULL};

    if (!PyArg_ParseTupleAndKeywords(args, kwargs, "y#y#y#|iii", kwlist, &data, &dataLen, &key, &keyLen,  &iv, &ivLen, &blockSize, &keySize, &mode)) {
        return NULL;
    }

    int encLen;
    byte* encData = encryptaes((const byte *)data, (const byte *)key, (const byte *)iv, (int)dataLen, (int)keyLen, (int)ivLen, blockSize, keySize, mode, &encLen);
    PyObject* result = PyBytes_FromStringAndSize((const char *)encData, encLen);
    free(encData);

    return result;
}

static PyMethodDef CRijndaelMethods[] = {
    {"decrypt", (PyCFunction)py_decrypt, METH_VARARGS | METH_KEYWORDS, "Decrypt data"},
    {"encrypt", (PyCFunction)py_encrypt, METH_VARARGS | METH_KEYWORDS, "Encrypt data"},
    {NULL, NULL, 0, NULL}
};

static struct PyModuleDef crijndaelmodule = {
    PyModuleDef_HEAD_INIT,
    "crijndael",
    NULL,
    -1,
    CRijndaelMethods
};

PyMODINIT_FUNC PyInit_crijndael(void) {
    return PyModule_Create(&crijndaelmodule);
}


