# Helper function to create CheckConstraint compatible with Django 5.x and 6.x
def create_check_constraint(name, q_condition):
    """Create CheckConstraint compatible with both Django 5.x and 6.x.

    Django 6.0+ uses 'condition' parameter, older versions use 'check'.
    """
    django_version = django.VERSION
    if django_version >= (6, 0):
        # Django 6.0+ uses 'condition'
        return models.CheckConstraint(name=name, condition=q_condition)
    else:
        # Django < 6.0 uses 'check'
        return models.CheckConstraint(name=name, check=q_condition)


