def generate_random_name():
    """Generate a random name in the style of AnnoyingDolphin10."""
    adjective = random.choice(ADJECTIVES)
    noun = random.choice(NOUNS)
    number = random.randint(1, 100)
    return f"{adjective}{noun}{number}"
